mapper.update:
UPDATE product SET product_name = #{productName},
product_price = #{productPrice},
product_qty = #{productQty}
WHERE
product_id = #{productId}


		UPDATE product
		SET product_qty = product_qty - #{orderProductQty}
      	WHERE product_id = #{productId}


		UPDATE product
		SET product_qty = product_qty + #{orderProductQty}
      	WHERE product_id = #{productId}
